import { action, makeAutoObservable } from "mobx";


class ActionStore {
    data = []
    status = "loading"

    constructor() {
        makeAutoObservable(this)
    }

    getAssestsData () {
        fetch('https://www.cryptingup.com/api/assets').then(
        this.status = "loaded",
        action(resp => {
            console.log(resp)
            
        })  

        )
    }

}

export default ActionStore;
