import { action, makeAutoObservable, runInAction } from "mobx";


class WhenReactionStore {
    count = 10
    msg = ""

    constructor() {
        makeAutoObservable(this)

    }

    showCountStatus() {
        
        if(this.isCounterTen) {
            return this.msg = "hello"

        }
        else {
            return msg = "h"
        }
    }
    
    get isCounterTen() {
        return this.count == 10
    }

}
export default WhenReactionStore;
