import React from 'react'
import '../CSS/main.css'

function Loan({store}) {

    const sendToStore = (event) => { 
        store.calculateIntrest(event.target.value,12)
       
    }

    return(
        <>
            <div class = "example">

                <div>

                    <input onChange = {sendToStore} ></input>    
                    <div>Intrest Amount : {store.intrestAmount}</div>
                 </div>


            </div>

        </>
    )

}

export default Loan;
