import { autorun } from 'mobx'
import { observer } from 'mobx-react-lite'
import React from 'react'
import '../CSS/main.css'

function Reaction({store}) {
    const[display, setDisplay] = React.useState(false)
    autorun(() => {
        setDisplay(true)
    })

    const displayAccuountAction = () => {
        return <div>Deposited</div>
    }
    return (
        <>
        <div class = "example">
            <div>{store.bankBalance}</div>
            <button onClick= {() => store.depositBalance(100)}>Deposit</button>
            <div>{display ? <div>Deposited</div> : <></>}</div>
        </div>
        </>
    )
}

export default observer(Reaction)
