      buyers = []
      buyHandlers = []
    
     constructor() {
        makeAutoObservable(this)
    }
    
    addStock(data) {
        this.stocks.push(data)
    }

    buyNifty50Options() {
        const nifty50 = new Nifty50Options(this)
        this.buyHandlers.push(nifty50)
        return nifty50
    }
    
    buyNiftyDerivative() {
        const niftyDerivative = new NiftyDerivative()
        this.buyHandlers = []
        return niftyDerivative;
    }



    buyStock(data, name) {
        data.buyer = name 
        this.buyers.push(data)
        this.changeQuantity(data)
        console.log(this.stocks)
        console.log(this.buyers)
    }

    changeQuantity(data) {
        this.stocks.forEach((objData) => { if(objData.nameOfStock == data.nameOfStock) { 
            objData.quantity = objData.quantity - data.quantity
        } })
    }

}

export default StockStore;
