import { action, makeAutoObservable, runInAction, when } from "mobx";


class WhenReactionStore {
    count = 0   
    msg = ""

    constructor() {
        makeAutoObservable(this)
        when(() => this.isCounterTen , () => this.dispose())
      

    }

    inc() {
        this.count= this.count + 1
        when(() => this.isCounterMoreThenTen , () => this.dispose())
    }

    dispose() {
        console.log("hello i got triggered")
        this.count = 0
    }

    showCountStatus() {

        console.log(this.isCounterTen)

        
        if(this.isCounterTen) {
            return this.msg = "hello"

        }
        else {
            return this.msg = "h"
        }
    }
    
    get isCounterTen() {
        
        return this.count > 10
    }

    get isCounterMoreThenTen() {
        return this.count >= 10 
    }

}
export default WhenReactionStore;
