import { action, makeAutoObservable } from "mobx";


class ActionStore {
    data = []
    status = "loading"

    constructor() {
        makeAutoObservable(this)
    }

    getAssestsData () {
        fetch('https://www.cryptingup.com/api/assets').then(
       
        action(resp => {
            console.log(resp)
            if(resp.status == 200) {
                this.status = "loaded"
            }
            
        })  

        )
    }

}

export default ActionStore;
