import { makeAutoObservable } from "mobx";
import Nifty50 from "./Nifty50";

class StockStore {
     stocks = [{nameOfStock : "INFY" , index : "Nifty50", quantity : 150}, {nameOfStock : "United" , index : "Nifty200" , quantity : 336}]
      buyers = []
      buyHandlers = []
    
     constructor() {
        makeAutoObservable(this)
    }
    
    addStock(data) {
        this.stocks.push(data)
    }

    buyNifty50Stock() {
        const nifty50 = new Nifty50(this)
        this.buyHandlers.push(nifty50)
        return nifty50
    }


    buyStock(data, name) {
        data.buyer = name 
        this.buyers.push(data)
        this.changeQuantity(data)
    }

    changeQuantity(data) {
        this.stocks.forEach((objData) => { if(objData.nameOfStock == data.name) { 
            objData.quantity = objData.quantity - data.quantity
        } })
    }

}

export default StockStore;
