import { makeAutoObservable } from "mobx";


class ComputedStore  {
    loanAmount = 0
    intrest = 0.078
    noMonths = 0
    constructor() {
        makeAutoObservable(this)
    }

    calculateIntrest(loan,noOfMonths) {
        this.loanAmount = loan
        this.noMonths = noOfMonths
    }

    showIntrest() {
       return  this.intrestAmount
    }

    get intrestAmount() {
        return this.loanAmount * this.intrest
    }

    get months () {
        return this.intrestAmount() / this.noMonths
    }
}

export default ComputedStore
