    constructor() {
        makeAutoObservable(this)
    }

    getAssestsData () {
        fetch('https://www.cryptingup.com/api/assets').then(
       
        action(resp => {  // every step (tick) that updates observables in an asynchronous process should be marked as action.
            console.log(resp)
            if(resp.status == 200) {
                this.status = "loaded"
            }
            
        })  

        )
    }

   async  useRunInAction () {
