import { makeAutoObservable } from "mobx";



class ReactionStore {

    bankBalance = 0
    msg =""

    constructor() {
        makeAutoObservable(this)
    }


    depositBalance(amount) {

        this.bankBalance = this.bankBalance+amount
    }


}

export default ReactionStore
